import React from 'react'
import '../scss/form.scss'

function form({yeniKurs}) {
 const formSubmit = (e) => {
 e.preventDefault();
 yeniKurs(
    {
        id: Math.round((Math.random()*10000)),
        corseTitle: "HTML",
        courseImg: "https://camo.githubusercontent.com/da7acacadecf91d6dc02efcd2be086bb6d78ddff19a1b7a0ab2755a6fda8b1e9/68747470733a2f2f63646e2e6a7364656c6976722e6e65742f67682f64657669636f6e732f64657669636f6e2f69636f6e732f68746d6c352f68746d6c352d6f726967696e616c2e737667",
        courseSubTitle:"HTML Course",
        courseDescription:"Lorem, ipsum dolor sit amet consectetur adipisicing."

    }
 )
 }

  return (
    <div className='formBox'>
        <form onSubmit={formSubmit}>
           
            <input type="text" placeholder='Photo URL'/>
            <input type="text" placeholder='Course Name'/>
            <input type="text" placeholder='Course Summary Info'/>
            <input type="text" placeholder='Course Info URL'/>
            <input type="submit" value="Add Course" />
        </form>

    </div>
  )
}

export default form
