function App() {
  const gameReguar = [
    {
      id:1.1,
      gameName: "Sek Sek",
      minGamePerson: 2,
      minAge:7
    },
    {
      id:1.2,
      gameName: "Yakan Top",
      minGamePerson: 4,
      minAge:7
    },
    {
      id:1.3,
      gameName: "Uzun Eşşek",
      minGamePerson: 5,
      minAge:11
    },
    {
      id:1.4,
      gameName: "İp Atlama",
      minGamePerson: 3,
      minAge:8
    },
    {
      id:1.5,
      gameName: "Çelik Çomak",
      minGamePerson: 4,
      minAge:8
    }]
    const pcGameReguar = [
      {
        id:2.1,
        gameName: "GTA",
        minGamePerson: 1,
        minAge:8
      },
      {
        id:2.2,
        gameName: "GTA-2",
        minGamePerson: 1,
        minAge:8
      },
      {
        id:2.3,
        gameName: "GTA-3",
        minGamePerson: 1,
        minAge:8
      },
      {
        id:2.4,
        gameName: "GTA-4",
        minGamePerson: 1,
        minAge:8
      },
      {
        id:2.5,
        gameName: "GTA-5",
        minGamePerson: 1,
        minAge:8
      }
    ]
    const tvReguar = [
      {
        id:3.1,
        gameName: "Yetenek Sizsiniz",
        minGamePerson: 1,
        minAge:6
      },
      {
        id:3.2,
        gameName: "Kim Milyoner Olmak İster",
        minGamePerson: 1,
        minAge:18
      },
      {
        id:3.3,
        gameName: "Hugo",
        minGamePerson: 1,
        minAge:10
      },
      {
        id:3.4,
        gameName: "Survivor",
        minGamePerson: 16,
        minAge:18
      },
      {
        id:3.5,
        gameName: "Yemekteyiz",
        minGamePerson:51,
        minAge:18
      }
    ]
  const fullGame = gameReguar.concat(pcGameReguar.concat(tvReguar));
  return (
    <div className="App" style={{textAlign:"center"}}>


      <h3>Tüm Oyunlar</h3>
        <ul>
          {fullGame.map(eleman => 
            <li key={`T${eleman.id}`} >{`${eleman.gameName} oyununu oynaak için minimum ${eleman.minAge} yaşın olmalısınız bu oyun en az ${eleman.minGamePerson} kişi ile oynanır.`}</li>

            )}
        </ul>
      <h4>Sokak Oyunları</h4>
        <ul style={{listStyle:"none"}} >
          {gameReguar.map((elemen) => 
            <li key={elemen.id} >{elemen.gameName}</li>
          )}
        </ul>



      <h4>TV Yarışmaları kaç Kişi ile Oynanıyor?</h4>
        <ul style={{listStyle:"none"}}>
          {tvReguar.map(eleman =>
            <li key={eleman.id}>{eleman.gameName+" yarışması " + eleman.minGamePerson + " kişi ile oynanır"}</li>
            )}
        </ul>
    </div>
  );
}

export default App;
